public class exp4 {

	/**
	 * @param args
	 * overloading
	 */
	 void sum(int a,int b){System.out.println(a+b);}  
	  void sum(int a,int b,int c){System.out.println(a+b+c);}  
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 exp4 obj=new exp4();  
		  obj.sum(10,10,10);  
		  obj.sum(20,20);  
	}

}
	
